{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "Image",
                                    "weight": "Bolder",
                                    "text": "**Space Request**",
                                    "url": "data:image/png;base64,",
                                    "width": "60px",
                                    "height": "60px"
                                }
                            ],
                            "width": "60px",
                            "minHeight": "60px"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "Container",
                                    "items": [
                                        {
                                            "type": "TextBlock",
                                            "text": "Provision Assist",
                                            "wrap": true,
                                            "size": "ExtraLarge",
                                            "weight": "Bolder",
                                            "horizontalAlignment": "Left"
                                        }
                                    ],
                                    "style": "default",
                                    "verticalContentAlignment": "Bottom"
                                }
                            ],
                            "width": "stretch",
                            "horizontalAlignment": "Center",
                            "verticalContentAlignment": "Bottom"
                        }
                    ]
                },
                {
                    "type": "Container",
                    "style": "attention",
                    "items": [
                        {
                            "type": "TextBlock",
                            "text": "Space Already Exists ‚ùå",
                            "wrap": true,
                            "size": "Large"
                        }
                    ]
                }
            ],
            "bleed": true,
            "backgroundImage": {
                "url": "data:image/png;base64,"
            }
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "ExtraLarge",
                                    "text": "@{triggerBody()?['Title']}",
                                    "wrap": true
                                }
                            ],
                            "width": "stretch"
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "text": "A space matching your request for an '@{triggerBody()?['SpaceType']?['Value']}' - '@{triggerBody()?['Title']}' already exists.",
                    "wrap": true,
                    "weight": "Bolder"
                },
                {
                    "type": "TextBlock",
                    "text": "This space may be active or in the recycle bin.",
                    "wrap": true,
                    "weight": "Bolder"
                },
                {
                    "type": "TextBlock",
                    "text": "Either edit and resubmit your request or contact an Administrator for more details.",
                    "wrap": true
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "fallbackText": "This card requires Adaptive Cards v1.2 support to be rendered properly."
}

inputs.value:
{
  "request": {
    "Description": "@{triggerBody()?['Description']}",
    "Lcid": 1033,
    "Owner": "@{first(variables('OwnerUPNs'))}",
    "ShareByEmailEnabled": false,
@{if(equals(empty(triggerBody()?['SensitivityLabelId']),true),'',concat('"SensitivityLabel":"',triggerBody()?['SensitivityLabelId'],'",'))}
    "Title": "@{If(equals(variables('UseNamingConventions'), true), If(and(equals(empty(triggerBody()?['Prefix']), true), equals(empty(triggerBody()?['Suffix']), true)), triggerBody()?['Title'], concat(triggerBody()?['Prefix'], triggerBody()?['SiteAlias'], triggerBody()?['Suffix'])), triggerBody()?['Title'])}",
    "Url": "@{triggerBody()?['SiteURL']}",
    "WebTemplate": "@{variables('WebTemplate')}",
@{outputs('Build_sitedesign_JSON_properties')}
  }
}

